import { Component } from '@angular/core';
import { TerrainModule } from 'src/app/models/Terrain/terrain/terrain.module';
import { TerrainService } from 'src/app/services/terrain.service';

@Component({
  selector: 'app-client',
  templateUrl: './client.component.html',
  styleUrls: ['./client.component.css']
})
export class ClientComponent {
  terrainModule:TerrainModule[]=[];

  constructor(private terrainservice:TerrainService,
             ){

  }
  ngOnInit(): void {
    this.GetAll();
  }

  GetAll(){
    this.terrainservice.GetAll().subscribe(item=>{
      this.terrainModule=item;
      console.log(this.terrainModule);
    },error=>{
      console.log(error);
    })
  }
}
<div class="container">
  <div class="row my-3">
    <div class="col-md-6">
      <!-- You can add content or elements here if needed -->
    </div>
    <div class="col-md-8 text-right">
      <!-- You can add content or elements here if needed -->
    </div>
  </div>
  <div class="row">
    <div class="col-md-6">
      <form class="card border-pink mb-3 mx-auto p-3">
        <div class="card-header text-center" style="background: rgb(63,94,251); background: radial-gradient(circle, rgba(63,94,251,1) 0%, rgba(252,70,107,1) 100%); color: #FFFFFF;">
          <span class="material-icons" style="vertical-align: middle;">mail</span> Contact Form
        </div>
        <div class="card-body">
          <div class="form-group">
            <label for="name" class="form-label">Name:</label>
            <input type="text" id="name" class="form-control">
          </div>
          <div class="form-group">
            <label for="email" class="form-label">Email:</label>
            <input type="email" id="email" class="form-control">
          </div>
          <div class="form-group">
            <label for="company" class="form-label">Company Name:</label>
            <input type="text" id="company" class="form-control">
          </div>
          <div class="form-group">
            <label for="phone" class="form-label">Contact Number:</label>
            <input type="text" id="phone" class="form-control">
          </div>
          <div class="form-group">
            <label for="message" class="form-label">Message:</label>
            <textarea id="message" class="form-control" rows="4"></textarea>
          </div>
          <div class="text-center">
            <button type="submit" class="btn btn-primary">Submit</button>
          </div>
        </div>
      </form>
    </div>
  </div>
</div>
